/**
 * Sends an empty message in a variance pattern depending on the settings.
 *
 * @id com.eviware.Variance
 * @help http://www.loadui.org/Generators/variance-component.html
 * @category generators
 * @nonBlocking true
 */

import java.util.concurrent.TimeUnit

def FUNCTIONS = [
        'Saw-tooth': { amp, progress -> amp * progress - amp / 2 },
        'Sine-wave': { amp, progress -> amp / 2 * Math.sin(2 * Math.PI * progress) },
        'Square'   : { amp, progress -> progress < 0.5 ? -amp / 2 : amp / 2 }
]

def UNITS = [
        'Sec' : TimeUnit.SECONDS,
        'Min' : TimeUnit.MINUTES,
        'Hour': TimeUnit.HOURS
]

//Properties
createProperty('rate', Long, 10)
createProperty('unit', String, 'Sec')
createProperty('shape', String, 'Saw-tooth')
createProperty('amplitudePercent', Long, 50)
createProperty('period', Long, 60)

currentRate = 0
startTime = System.currentTimeMillis()

calculateRate = {
    def per = period.value * 1000
    def progress = ((System.currentTimeMillis() - startTime) % per) / per
    def amplitude = rate.value * amplitudePercent.value / 100
    def stepValue = FUNCTIONS[shape.value](amplitude, progress)
    def newRate = Math.round(rate.value + stepValue)
    //notify("New rate is $newRate base value $rate.value percent $amplitudePercent.value amp $amplitude step value: $stepValue")
    if (currentRate != newRate) {
        try {
            def timePassed = 0;
            if (currentRate != 0) timePassed = (future && !future.cancelled) ? UNITS[unit.value].toMicros(1) / currentRate - future.getDelay(TimeUnit.MICROSECONDS) : 0
            currentRate = newRate
            //notify("Current rate: $currentRate")
            schedule(timePassed)
        } catch (e) {
            e.printStackTrace()
        }
    }
}

int userCount = 0
future = null
pollFuture = null
initialize = {
    userCount = 0
    currentRate = 0

    future?.cancel(true)
    future = null
    pollFuture?.cancel(true)
    pollFuture = null
    startTime = System.currentTimeMillis()
    pollFuture = scheduleAtFixedRate(calculateRate, 0, 500, TimeUnit.MILLISECONDS)
}

schedule = { timePassed ->
    /*
    if (future != null)
        notify "Clearing"
        */
    future?.cancel(true)
    future = null
    if (stateProperty.value && currentRate > 0) {
        def triggerDelay = UNITS[unit.value].toMicros(1) / currentRate
        def initialDelay = Math.max(0.0, triggerDelay - timePassed)
        //notify "start $triggerDelay initial delay $initialDelay rate: $currentRate"
        future = scheduleAtFixedRate({
            if (lastAction != "START") {
                future?.cancel(true)
                return
            }
            trigger()
            userCount++
            //notify "Simulated $userCount"
        }, initialDelay, triggerDelay, TimeUnit.MICROSECONDS)
    }
}

onAction("START") {
    lastAction = "START"
    initialize()
}
onAction("STOP") {
    lastAction = "STOP"
    pollFuture?.cancel(true)
    pollFuture = null
    future?.cancel(true)
    future = null
}

onAction("RESET") {
    future?.cancel(true)
    future = null
    startTime = System.currentTimeMillis()
}
